// Number Triangle // This is a solution to lab 0, Summer 2013// no semi-colon terminates the C-preprocessor directives#include <stdio.h>#define LEFTSPACE 50void printTriangle(int);int factorial(int);int f(int, int);int main(void){        int height;        /* input height of number triangle */    printf("Input height of number traingle: ");    scanf("%d", &height);        printTriangle(height);    //printf("%d", f(5,4));    // printf("%d\n", LEFTSPACE);}void printTriangle(int h) {      int i, j, k; //loop variables               for (i = 0; i < h; i++){           for (j = 0; j <= (LEFTSPACE - i); j++) printf(" "); // initial spaces          for (k = 0; k <= i; k++)            {if (f(i, k) < 0) printf("Erroneous input \n");              else printf("%d  ", f(i, k));            } // print the numbers, one space apart           printf("\n");              } // end for     return;} // end of printTriangleint f(int n, int m){      if (n < m)  return -1;    else return (factorial(n)/(factorial(m)*factorial(n-m)));}  // end of f(x,y)int factorial(int n) {    if (n==0) return 1;    else return (n*factorial(n-1));} // end of factorial